<?phpclass block_newsforuns_edit_form extends block_edit_form {	 	protected function specific_definition($mform) {		//print_r($this->block->fname);		$mform->addElement('header', 'configheader', get_string('blocksettings', 'block')); //caixa das configuaes		$mform->addElement('text',   'config_title', get_string('blocktitle','block_newsforuns'));				$mform->addElement('select', 'config_fname', get_string('nomeforuns','block_newsforuns'),$this->block->fname);        $mform->setType('config_fname', PARAM_INT);				$mform->addElement('text',   'config_itens', get_string('itens','block_newsforuns'));		$mform->setDefault('config_itens', 5);        $mform->setType('config_itens', PARAM_INT);	}}?>